require_once('workflows.php');
$w = new Workflows();
$query = urlencode( "{query}" );
$url = "https://domainr.p.mashape.com/v1/search?mashape-key=AkuVf2rVXOmshqvTGDKW7TmvKRtfp1kdBUYjsnqnrJp3ZiJCyF&q=$query";
$domains = $w->request( $url );
$domains = json_decode( $domains );

function available($sub) {
  if($sub == 'available' ) {
    $icon = 'icons/green.png';
  }
  elseif( $sub == 'maybe' ) {
    $icon = 'icons/yellow.png';
  }
  elseif( $sub == 'tld' ) {
    $icon = 'icons/gray.png';
  }
  elseif( $sub == 'taken' || $sub == 'unavailable' ) {
    $icon = 'icons/red.png';
  }
  return $icon;
}

foreach ( $domains->results as $domainr ) {
  $w->result(
    $domainr->register_url,
    $domainr->register_url,
    $domainr->domain.$domainr->path,
    $domainr->availability,
    available($domainr->availability)
  );
};

echo $w->toxml();
